// String retourne le SVG en tant que string
func (i Icon) String() string {
	return string(i)
}

// WithClass returns the icon with a custom CSS class
func (i Icon) WithClass(class string) string {
	svg := string(i)
	// Replace the default class
	return replaceClass(svg, class)
}

// WithSize returns the icon with a specific size
func (i Icon) WithSize(size string) string {
	sizeClass := "w-" + size + " h-" + size
	return i.WithClass(sizeClass)
}

func replaceClass(svg, newClass string) string {
	// Simple class replacement - finds class="..." and replaces
	start := 0
	for i := 0; i < len(svg)-6; i++ {
		if svg[i:i+6] == "class=" {
			start = i + 7
			break
		}
	}
	if start == 0 {
		return svg
	}

	end := start
	for end < len(svg) && svg[end] != '"' {
		end++
	}

	return svg[:start] + newClass + svg[end:]
}

